import defaultUser from '../utils/default-user';
import api from '../service/apiIntegration'

interface User {
  id: string;
	name: string;
	email: string;
	createAt: string
	token: string;
}

export async function signIn(email: string, password: string) {
  let isOk = true;
  let message = '';
  try {
    // Send request
    const user = await api.signIn(email, password).catch((err) => { throw err; });

    if (user?.data?.err) {
      isOk = false;
      message = user.data.err;

      return {
        isOk: isOk,
        message: message
      };
    }

    const userData: User = user;

    localStorage.setItem('token', userData.token);
    localStorage.setItem('email', userData.email);

    return {
      isOk: isOk,
      data: {
        email: userData.email,
        token: userData.token,
        avatarUrl: 'data:image/jpeg;base64,' }
    };
  }
  catch(err) {
    isOk = false;
    // console.log(err);
    return {
      isOk: isOk,
      message: message
    };
  }
}

export async function getUser() {
  try {
    const user = await api.getUser()
    // Send request

    if (user?.isOk === false) {
      return {
        isOk: false
      };
    }

    return {
      isOk: true,
      data: {
          email: user?.email,
          token: user?.token,
          avatarUrl: 'data:image/jpeg;base64,' }
    };
  }
  catch {
    return {
      isOk: false
    };
  }
}

export async function createAccount(email: string, password: string, name: string) {
  let isOk = true;
  let message = '';
  try {
    // Send request
    const user = await api.createAccount(email, password, name).catch((err) => { throw err; });


    if (user?.data?.err) {
      isOk = false;
      message = user.data.err;

      return {
        isOk: isOk,
        message: message
      };
    }

    const userData = user;

    localStorage.setItem('token', userData.token);
    localStorage.setItem('email', userData.email);


    return {
      isOk: isOk,
      data: defaultUser
    };
  }
  catch(err) {
    isOk = false;
    console.log(err);
    return {
      isOk: isOk,
      message: message
    };
  }
}

