export async function resetPassword(email: string) {
  let isOk = true;

  try {
    // Send request
    console.log(email);

    const res = await api.sendEmail(email, 'forgot')

    if (res?.data?.err) {
      isOk = false;

      return {
        isOk: isOk,
        message: "Failed to reset password"
      };
    }

    return {
      isOk: true
    };
  }
  catch {
    return {
      isOk: false,
      message: "Failed to reset password"
    };
  }
}
